private function executeCommand(string $commandIdentifier, array $arrayInput = []): string
{
    // Workaround issue https://github.com/magento/magento2/issues/12844
    \Magento\TestFramework\Helper\Bootstrap::getObjectManager()->configure(
        [
            'preferences' => [
                ltrim(
                    \Magento\Framework\Interception\ObjectManager\ConfigInterface::class,
                    '\\'
                ) => ltrim(
                    \Magento\TestFramework\ObjectManager\Config::class,
                    '\\'
                ),
            ],
        ]
    );
    $commands = \Magento\TestFramework\Helper\Bootstrap::getObjectManager()
        ->create(\Magento\Framework\Console\CommandListInterface::class)
        ->getCommands();

    $output = new BufferedOutput();
    $commands[$commandIdentifier]->run(
        new ArrayInput($arrayInput),
        $output
    );
    return $output->fetch();
}

