/**
 * @param string|string[] $handles
 * @return \Magento\Framework\View\LayoutInterface
 */
protected function getLayout($handles)
{
    /** @var \Magento\Framework\View\Result\Page $resultPage */
    $resultPage = $this->_objectManager->get(\Magento\Framework\View\Result\Page::class);
    $resultPage->addHandle('default');
    $resultPage->addHandle($handles);
    return $resultPage->getLayout();
}
